import React, { Component } from "react";
import ReactDOM from 'react-dom'
import axios from 'axios'


const axiosConfigGet = {
  method: "get",
}
const axiosConfigPost = {
  method: "post",
  url: "http://127.0.0.1:5000/post"
}

class GetBalance extends Component {
  constructor(props) {
    super(props);
    this.state = {
      account_addr: '',
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({
      [event.target.id]: event.target.value,
    })
  }

  handleSubmit = () => {
    axios({ ...axiosConfigGet, url: `http://127.0.0.1:5000/actions/gb/${this.state.account_addr}` })
      .then((res) => {
        console.log(`response from the server is ${res}`)
        this.setState((prevState) => ({ ...prevState, store: res.data.name }))
      })
      .catch((err) => {
        console.log(`error connecting the server is ${err}`)
      })
  }

  render() {
    return (
      <div>
        <h1 className="display-4 text-center mt-4"> Get Balance of an Account </h1>
        <h3 className="lead text-center">帳戶餘額查詢</h3>
        <br />
        <label htmlFor="title"> Account *</label>
        <input
          type="text"
          className="form-control"
          id="account_addr"
          placeholder="Address of the account"
          value={this.state.account_addr}
          onChange={this.handleChange}
          required
        />
        <br /><br />
        <button onClick={this.handleSubmit}>submit</button>
        <br />
        <h4>data from server</h4>
        <p>{this.state.store}</p>
      </div>
    )
  }
}

export default GetBalance;
