import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { Menu, Dropdown, Icon} from 'antd';
import ReactDOM from 'react-dom'

class GetBalance extends Component {
  constructor(props) {
    super(props);
    this.state = {
       account_addr: '',
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({
      [event.target.id]: event.target.value,
    })
  }

  handleSubmit(event) {
    alert('A New Transaction! \n From: ' + this.state.from_addr + '\nTo: ' + this.state.to_addr + '\nCointype: ' + this.state.cointype + '\nAmount: ' + this.state.amount);
    event.preventDefault();
  }

  render() {
   return (
      <div className="container">
      <fieldset disabled={this.props.loading}>
        <div className="row">
          <div className="col-md-8 m-auto">
            <h1 className="display-4 text-center mt-4"> Get Balance of an Account </h1>            
              <p className="lead text-center">
                 帳戶餘額查詢
              </p>            
            <form
              className="needs-validation"
              onSubmit={this.handleSubmit}
            >
              <div className="form-group">
                <label htmlFor="title"> Account *</label>
                <input
                  type="text"
                  className="form-control"
                  id="account_addr"
                  placeholder="Address of the account"
                  value={this.state.account_addr}
                  onChange={this.handleChange}
                  required
                />

              </div>
              <div className="mb-3">
                  <button type="submit" className="btn btn-primary">
                    Submit
                  </button>
              </div>
            </form>
          </div>
        </div>
      </fieldset>
    </div>
  )
}
}

 export default GetBalance;
