import React, { Component } from "react";
import ReactDOM from 'react-dom'
import axios from 'axios'

const axiosConfigGet = {
  method: "get",
}
const axiosConfigPost = {
  method: "post",
  url: "http://127.0.0.1:5000/post"
}

class CreateBlockchain extends Component {
  constructor(props) {
    super(props);
    this.state = {
      account_addr: '',
      cointype: '',
      amount: ''
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({
      [event.target.id]: event.target.value,
    })
  }

  handleSubmit = () => {
    axios({ ...axiosConfigGet, url: `http://127.0.0.1:5000/actions/${this.state.account_addr}/${this.state.cointype}/${this.state.amount}` })
      .then((res) => {
        console.log(`response from the server is ${res}`)
        this.setState((prevState) => ({ ...prevState, store: res.data.name }))
      })
      .catch((err) => {
        console.log(`error connecting the server is ${err}`)
      })
  }

  render() {
    return (
      <div>
        <h1 className="display-4 text-center mt-4"> Create Blockchain </h1>
        <h3 className="lead text-center"> 創造區塊鏈！</h3>

        <label htmlFor="title"> Account *</label>
        <input
          type="text"
          className="form-control"
          id="account_addr"
          placeholder="Address of the sender"
          value={this.state.account_addr}
          onChange={this.handleChange}
          required
        />
        <br />
        <label htmlFor="tags">Cointype *</label>
        <input
          type="text"
          className="form-control"
          id="cointype"
          placeholder="Type of the coin"
          value={this.state.cointype}
          onChange={this.handleChange}
          required
        />
        <br />
        <label htmlFor="tags">Total amount *</label>
        <input
          type="number"
          className="form-control"
          id="amount"
          placeholder="Amount of coin to transfer"
          value={this.state.amount}
          onChange={this.handleChange}
          required
        />
        <br />
        <button onClick={this.handleSubmit}>submit</button>

        <h4>data from server</h4>
        <p>{this.state.store}</p>
      </div>
    )
  }
}

export default CreateBlockchain;
