import React, { Component } from 'react';
import axios from 'axios'

const axiosConfig = {
   method: "get",
}
class EXample extends Component {
   constructor(props) {
      super(props);
      this.state = {entry: "", store: ""};
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
   }
   handleChange = (event) => {
      this.setState({
        [event.target.id]: event.target.value,
      })
    }
    handleSubmit = () => {
      axios({...axiosConfig, url: `http://127.0.0.1:5000/store/${this.state.entry}`})
         .then((res)=>{
            console.log(`response from the server is ${res}`)
            this.setState((prevState)=>({...prevState, store: res.data.name}))
         })
         .catch((err)=>{
            console.log(`error connecting the server is ${err}`)
         })
    }
   render() {
      return (
         <div>
            <label htmlFor="entry">entry</label>
            <input
               type="text"
               id="entry"
               placeholder="標題..."
               value={this.state.entry}
               onChange={this.handleChange}
            />
            <button onClick={this.handleSubmit}>sumbmit</button>
            <h4>data from serever</h4>
            <p>{this.state.entry}</p>
         </div>
      );
   }
}

export default EXample;
