import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { Menu, Dropdown, Icon} from 'antd';
import ReactDOM from 'react-dom'

class Send extends Component {
  constructor(props) {
    super(props);
    this.state = {
       from_addr: '',
       to_addr: '',
       cointype: '',
       amount: ''
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({
      [event.target.id]: event.target.value,
    })
  }

  handleSubmit(event) {
    alert('A New Transaction! \n From: ' + this.state.from_addr + '\nTo: ' + this.state.to_addr + '\nCointype: ' + this.state.cointype + '\nAmount: ' + this.state.amount);
    event.preventDefault();
  }

  render() {
   return (
      <div className="container">
      <fieldset disabled={this.props.loading}>
        <div className="row">
          <div className="col-md-8 m-auto">
            <h3 className="display-4 text-center mt-4"> Send coins to someone  </h3>            
              <h4 className="lead text-center">
                  區塊鏈交易！
              </h4>            
            <form
              className="needs-validation"
              onSubmit={this.handleSubmit}
            >
              <div className="form-group">
                <label htmlFor="title"> Sender * </label>
                <input
                  type="text"
                  className="form-control"
                  id="from_addr"
                  placeholder="The address of the sender"
                  value={this.state.from_addr}
                  onChange={this.handleChange}
                  required
                />

              </div>
              <div className="form-group">
                <label htmlFor="tags">Receiver *</label>
                <input
                  type="text"
                  className="form-control"
                  id="to_addr"
                  placeholder="The address of the receiver"
                  value={this.state.to_addr}
                  onChange={this.handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="tags">Cointype *</label>
                <input
                  type="text"
                  className="form-control"
                  id="cointype"
                  placeholder="The type of the coin"
                  value={this.state.cointype}
                  onChange={this.handleChange}
                  required
                />

              </div>
              <div className="form-group">
                <label htmlFor="tags">Amount *</label>
                <input
                  type="number"
                  className="form-control"
                  id="amount"
                  placeholder="The amount of coin to transfer"
                  value={this.state.amount}
                  onChange={this.handleChange}
                  required
                />

              </div>
              <div className="mb-3">
                  <button type="submit" className="btn btn-primary">
                    Submit
                  </button>
              </div>
            </form>
          </div>
        </div>
      </fieldset>
    </div>
  )
}
}

const mapStateToProps = state => ({
   state: state.reducerState
 });

 export default connect(
   mapStateToProps,
 )(Send);
