import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { Menu, Dropdown, Icon } from 'antd';
import ReactDOM from 'react-dom'

class AddCoin extends Component {
  constructor(props) {
    super(props);
    this.state = {
      account_addr: '',
      cointype: '',
      amount: ''
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({
      [event.target.id]: event.target.value,
    })
  }

  handleSubmit(event) {
    alert('A New Transaction! \n From: ' + this.state.from_addr + '\nTo: ' + this.state.to_addr + '\nCointype: ' + this.state.cointype + '\nAmount: ' + this.state.amount);
    event.preventDefault();
  }

  render() {
    return (
      <div>
        <h1 className="display-4 text-center mt-4"> Add new coin! </h1>
        <h3 className="lead text-center">
          創造新的幣別！
              </h3>
        <label htmlFor="title"> Account *</label>
        <input
          type="text"
          className="form-control"
          id="account_addr"
          placeholder="Address of the sender"
          value={this.state.account_addr}
          onChange={this.handleChange}
          required
        />
        <br />
        <label htmlFor="tags">Cointype *</label>
        <input
          type="text"
          className="form-control"
          id="cointype"
          placeholder="Type of the coin"
          value={this.state.cointype}
          onChange={this.handleChange}
          required
        />
        <br />
        <label htmlFor="tags">Total amount *</label>
        <input
          type="number"
          className="form-control"
          id="amount"
          placeholder="Amount of coin to transfer"
          value={this.state.amount}
          onChange={this.handleChange}
          required
        />
        <br /><br />
        <button onClick={this.handleSubmit}>submit</button>
        <br />
        <h4>data from server</h4>
        <p>{this.state.store}</p>
      </div>
    )
  }
}

const mapStateToProps = state => ({
  state: state.reducerState
});

export default connect(
  mapStateToProps,
)(AddCoin);
