import React, { Component } from "react";
import axios from 'axios'
import ReactDOM from 'react-dom'

const axiosConfigGet = {
  method: "get",
}
const axiosConfigPost = {
  method: "post",
  url: "http://127.0.0.1:5000/post"
}

class AddWallet extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      privateKey: '',
      address: ''
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({
      [event.target.id]: event.target.value,
    })
  }

  handleSubmit = () => {
    axios({ ...axiosConfigGet, url: `http://127.0.0.1:5000/actions/aw/${this.state.username}` })
      .then((res) => {
        console.log(`response from the server is ${res}`)
        this.setState((prevState) => ({ ...prevState, privateKey: res.data.privateKey }))
      })
      .catch((err) => {
        console.log(`error connecting the server is ${err}`)
      })
  }

  render() {
    return (
      <div>
        <h1 className="display-4 text-center mt-4"> Get Wallet! </h1>
        <h3 className="lead text-center">創造新的錢包！</h3>
        <label htmlFor="title"> Username *</label>
        <input
          type="text"
          className="form-control"
          id="username"
          placeholder="Name of the user"
          value={this.state.username}
          onChange={this.handleChange}
          required
        />
        <br /><br />
        <button onClick={this.handleSubmit}>submit</button>
        <br />
        <h4>data from server</h4>
        <p>{this.state.store}</p>
      </div>
    )
  }
}

export default AddWallet;
